import React, { useState, useEffect } from 'react';

function Quiz() {
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [result, setResult] = useState(null);

    useEffect(() => {
        fetch('/api/quiz')
            .then(response => response.json())
            .then(data => setQuestions(data));
    }, []);

    const handleAnswer = (answer) => {
        const correct = questions[currentQuestionIndex].answer === answer;
        setResult(correct ? 'Correct!' : 'Incorrect!');
        setCurrentQuestionIndex(currentQuestionIndex + 1);
    };

    if (questions.length === 0) return <div>Loading...</div>;
    if (currentQuestionIndex >= questions.length) return <div>Quiz completed!</div>;

    return (
        <div>
            <h2>{questions[currentQuestionIndex].question}</h2>
            <button onClick={() => handleAnswer(true)}>O</button>
            <button onClick={() => handleAnswer(false)}>X</button>
            {result && <p>{result}</p>}
        </div>
    );
}

export default Quiz;
