import React from 'react';
import { useNavigate } from 'react-router-dom'; // 페이지 이동을 위한 훅
import Header from './MainHeader';

const Main = () => {
  const navigate = useNavigate(); // navigate 훅

  const handleUploadClick = () => {
    navigate('/imageupload'); // '/imageupload' 경로로 이동
  };

  const handleQuizClick = () => {
    navigate('/quiz'); // '/quiz' 경로로 이동
  };

  return (
    <main style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh' }}>
      <Header />
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <p style={{ marginRight: '10px' }}>대중 교통 후불 카드 인증</p>
        <button 
          onClick={handleUploadClick} 
          style={{ marginLeft: '20px', cursor: 'pointer' }}>
          업로드
        </button>
      </div>
      <div style={{ display: 'flex', alignItems: 'center', marginTop: '20px' }}>
        <p style={{ marginRight: '10px' }}>퀴즈 풀기</p>
        <button 
          onClick={handleQuizClick} 
          style={{ marginLeft: '20px', cursor: 'pointer' }}>
          퀴즈
        </button>
      </div>
    </main>
  );
};

export default Main;


