import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode'; // JWT 디코딩을 위한 라이브러리
import '../styles/Calendar.css';

const Calendar = () => {
  const navigate = useNavigate();
  const [currentDate, setCurrentDate] = useState(new Date()); // 현재 날짜 관리
  const [checkedDates, setCheckedDates] = useState(new Set());
  const [email, setEmail] = useState(""); // 이메일 상태 추가

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decodedToken = jwtDecode(token); // JWT 디코딩
        setEmail(decodedToken.email); // 이메일을 상태에 저장
      } catch (error) {
        console.error('토큰 디코딩 실패:', error);
        navigate('/'); // 토큰이 유효하지 않으면 로그인 페이지로 리디렉트
        return;
      }
    } else {
      navigate('/'); // 토큰이 없으면 로그인 페이지로 리디렉트
      return;
    }

    // 이메일이 설정되면 체크인 날짜를 가져오는 함수 실행
    const fetchCheckedDates = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/attendance/all/${email}`);
        const dates = response.data.map(record => record.date);
        setCheckedDates(new Set(dates));
      } catch (error) {
        console.error('Failed to fetch checked-in dates', error);
      }
    };

    if (email) {
      fetchCheckedDates(); // email이 설정된 경우에만 fetchCheckedDates 호출
    }
  }, [email, navigate]);

  const getDaysInMonth = (month, year) => new Date(year, month + 1, 0).getDate();
  const getFirstDayOfMonth = (month, year) => new Date(year, month, 1).getDay();

  const handleDayClick = async (day) => {
    const today = new Date();
    const todayYear = today.getFullYear();
    const todayMonth = today.getMonth();
    const todayDate = today.getDate();
    
    const selectedDate = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
    const selectedYear = selectedDate.getFullYear();
    const selectedMonth = selectedDate.getMonth();
    const selectedDay = selectedDate.getDate();

    // Check if the clicked date is today
    if (todayYear === selectedYear && todayMonth === selectedMonth && todayDate === selectedDay) {
      const dayStr = selectedDate.toISOString().split('T')[0];

      if (!checkedDates.has(dayStr)) {
        try {
          // Send the check-in data to the server
          await axios.post('http://localhost:8080/api/attendance/check-in', {
            emailId: email,
            date: dayStr,
            checked: true
          }, {
            headers: { 'Content-Type': 'application/json' }
          });

          // Update the checkedDates state
          setCheckedDates(new Set(checkedDates).add(dayStr));
        } catch (error) {
          console.error('Failed to save check-in', error);
        }
      } else {
        alert('이미 출석 했습니다.');
      }
    } else {
      alert('오늘만 출석 가능합니다.');
    }
  };

  const renderCalendar = () => {
    const daysInMonth = getDaysInMonth(currentDate.getMonth(), currentDate.getFullYear());
    const firstDay = getFirstDayOfMonth(currentDate.getMonth(), currentDate.getFullYear());

    const weeks = [];
    let days = [];

    for (let i = 0; i < firstDay; i++) {
      days.push(<td key={`empty-${i}`} className="empty"></td>);
    }

    for (let day = 1; day <= daysInMonth; day++) {
      const dayStr = new Date(currentDate.getFullYear(), currentDate.getMonth(), day).toISOString().split('T')[0];
      days.push(
        <td key={day} className="day" onClick={() => handleDayClick(day)}>
          {day}
          {checkedDates.has(dayStr) && (
            <div className="award-icon">
              <i className="fas fa-seedling"></i>
            </div>
          )}
        </td>
      );

      if ((firstDay + day) % 7 === 0) {
        weeks.push(<tr key={`week-${weeks.length}`}>{days}</tr>);
        days = [];
      }
    }

    if (days.length > 0) {
      weeks.push(<tr key={`week-${weeks.length}`}>{days}</tr>);
    }

    return weeks;
  };

  return (
    <div className="calendar">
      <div className="header">
        <span className="month-year">
          {currentDate.getFullYear()}년 {currentDate.toLocaleDateString('ko-KR', { month: 'long' })}
        </span>
      </div>
      <table>
        <thead>
          <tr>
            <th>일</th>
            <th>월</th>
            <th>화</th>
            <th>수</th>
            <th>목</th>
            <th>금</th>
            <th>토</th>
          </tr>
        </thead>
        <tbody>
          {renderCalendar()}
        </tbody>
      </table>
    </div>
  );
};

export default Calendar;
