import React from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header'; 
import './styles/Main.css'; // CSS 파일을 임포트
import ProgressBar from './components/main/ProgressBar';

const Main = () => {
  const [progress, setProgress] = useState(30); // 초기 진행 상태 30%

  const navigate = useNavigate();

  const handleUploadClick = () => {
    navigate('/imageupload');
  };

  const handleQuizClick = () => {
    navigate('/quiz');
  };

  return (
    <div className="main-page">
      <Header />
      <h1>진행 상태</h1>
      <ProgressBar progress={progress} />
       {/* 버튼을 통해 진행 상태를 변경 */}
       <button onClick={() => setProgress(progress + 10)}>진행 더하기</button>
      <button onClick={() => setProgress(progress - 10)}>진행 빼기</button>

      <main className="main-content">
        <div className="action-group">
          <p className="action-text">대중 교통 후불 카드 인증</p>
          <button 
            onClick={handleUploadClick} 
            className="action-button">
            업로드
          </button>
        </div>
        <div className="action-group">
          <p className="action-text">친환경 퀴즈 풀기</p>
          <button 
            onClick={handleQuizClick} 
            className="action-button">
            풀기
          </button>
        </div>
      </main>
    </div>
  );
};

export default Main;
