import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CheckList = ({ email }) => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");

  useEffect(() => {
    // Debugging: Log the received email to check if it's properly passed
    console.log('Email received:', email);

    if (!email) {
      console.error('Email is undefined, redirecting to login');
      navigate('/main');  // Redirect to login if email is not available
      return;
    }

    const fetchTasks = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/checklist/user/${email}`, {
          headers: { 'Content-Type': 'application/json' }
        });
        console.log('Tasks fetched successfully:', response.data);
        setTasks(response.data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [email, navigate]);

  const addTask = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/checklist/add', 
        { email, text: newTask }, 
        { headers: { 'Content-Type': 'application/json' } }
      );
      console.log('Task added:', response.data);
      setTasks([...tasks, response.data]);
      setNewTask("");
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const deleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/checklist/delete/${id}`);
      console.log('Task deleted:', id);
      setTasks(tasks.filter(task => task.id !== id));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h2>{`${email}'s Checklist`}</h2>
      <ul>
        {tasks.map(task => (
          <li key={task.id}>
            {task.text}
            <button onClick={() => deleteTask(task.id)}>Delete</button>
          </li>
        ))}
      </ul>
      <input 
        type="text" 
        value={newTask} 
        onChange={e => setNewTask(e.target.value)} 
        placeholder="New Task" 
      />
      <button onClick={addTask}>Add Task</button>
    </div>
  );
};

export default CheckList;

