import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/Calendar.css';

const Calendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [checkedDates, setCheckedDates] = useState(new Set());
  const [userEmail, setUserEmail] = useState(''); // 사용자 이메일 저장을 위한 상태

  useEffect(() => {
    // 컴포넌트가 마운트될 때 사용자 이메일과 출석 정보를 불러오는 함수
    const fetchUserEmailAndAttendance = async () => {
      try {
        // 사용자 이메일 가져오기
        const userResponse = await axios.get('/api/user/email');
        const email = userResponse.data.emailId;
        setUserEmail(email);

        //console.log('Fetched user email:', email); // 이메일 로그 출력

        // 사용자의 출석 정보 가져오기
        const attendanceResponse = await axios.get(`/api/attendance/today/${email}`);
        const attendanceDates = attendanceResponse.data.map(record => record.date);

        // 가져온 출석 정보로 checkedDates 업데이트
        setCheckedDates(new Set(attendanceDates));
      } catch (error) {
        console.error('사용자 이메일 또는 출석 정보 가져오기 실패', error);
      }
    };

    fetchUserEmailAndAttendance();
  }, []);

  const getDaysInMonth = (month, year) => new Date(year, month + 1, 0).getDate();
  const getFirstDayOfMonth = (month, year) => new Date(year, month, 1).getDay();

  const handlePrevMonth = () => {
    const newDate = new Date(currentDate.getFullYear(), currentDate.getMonth() - 1);
    setCurrentDate(newDate);
  };

  const handleNextMonth = () => {
    const newDate = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1);
    setCurrentDate(newDate);
  };

  const handleDayClick = async (day) => {
    const today = new Date();
    const todayYear = today.getFullYear();
    const todayMonth = today.getMonth();
    const todayDate = today.getDate();
    
    const selectedDate = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
    const selectedYear = selectedDate.getFullYear();
    const selectedMonth = selectedDate.getMonth();
    const selectedDay = selectedDate.getDate();
  
    // 오늘 날짜를 클릭한 경우
    if (todayYear === selectedYear && todayMonth === selectedMonth && todayDate === selectedDay) {
      const dayStr = selectedDate.toISOString().split('T')[0]; // 선택한 날짜를 'YYYY-MM-DD' 형식으로 변환

      if (!checkedDates.has(dayStr)) {
        try {
          console.log('Posting check-in data:', {
            emailId: userEmail,
            date: dayStr,
            checked: true
          });

          // 출석 정보를 데이터베이스에 저장
          const response = await axios.post('/api/attendance/check-in', {
            emailId: userEmail,
            date: dayStr,
            checked: true
          });

          console.log('Response from server:', response.data);

          // 새로운 출석 정보를 추가하여 checkedDates 업데이트
          setCheckedDates(new Set(checkedDates).add(dayStr));
        } catch (error) {
          console.error('출석 기록 저장 실패', error);
        }
      } else {
        alert('출석은 하루에 한 번 가능합니다.');
      }
    } else {
      alert('오늘이 아닙니다.');
    }
  };

  const renderCalendar = () => {
    const daysInMonth = getDaysInMonth(currentDate.getMonth(), currentDate.getFullYear());
    const firstDay = getFirstDayOfMonth(currentDate.getMonth(), currentDate.getFullYear());

    const weeks = [];
    let days = [];

    for (let i = 0; i < firstDay; i++) {
      days.push(<td key={`empty-${i}`} className="empty"></td>);
    }

    for (let day = 1; day <= daysInMonth; day++) {
      const dayStr = new Date(currentDate.getFullYear(), currentDate.getMonth(), day).toISOString().split('T')[0];
      days.push(
        <td key={day} className="day" onClick={() => handleDayClick(day)}>
          {day}
          {checkedDates.has(dayStr) && (
            <div className="award-icon">
              <i className="fas fa-seedling"></i>
            </div>
          )}
        </td>
      );

      if ((firstDay + day) % 7 === 0) {
        weeks.push(<tr key={`week-${weeks.length}`}>{days}</tr>);
        days = [];
      }
    }

    if (days.length > 0) {
      weeks.push(<tr key={`week-${weeks.length}`}>{days}</tr>);
    }

    return weeks;
  };

  return (
    <div className="calendar">
      <div className="header">
        <span className="month-year">
          {currentDate.getFullYear()}년 {currentDate.toLocaleDateString('ko-KR', { month: 'long' })}
        </span>
        <button onClick={handleNextMonth}>다음</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>일</th>
            <th>월</th>
            <th>화</th>
            <th>수</th>
            <th>목</th>
            <th>금</th>
            <th>토</th>
          </tr>
        </thead>
        <tbody>
          {renderCalendar()}
        </tbody>
      </table>
    </div>
  );
};

export default Calendar;
