import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header'; 
import './styles/Main.css'; // CSS 파일을 임포트
import ProgressBar from '../components/main/ProgressBar'; // 경로 수정 필요
  
const Main = () => {
  const navigate = useNavigate();
  
  // 진행 상태를 관리하기 위한 상태 변수 추가
  const [progress, setProgress] = useState(50); // 예시로 50%로 설정

  const handleUploadClick = () => {
    navigate('/imageupload');
  };

  const handleQuizClick = () => {
    navigate('/quiz');
  };

  return (
    <div className="main-page">
      <Header/>
      <div className="main-content"> {/* 상단 여백 추가 */}
      <h1>진행 상태</h1>
      {/* 진행 상태를 표시하는 ProgressBar 컴포넌트 */}
      <ProgressBar progress={progress} />

      {/* <main className="main-content">
        <div className="action-group">
          <p className="action-text">대중 교통 후불 카드 인증</p>
          <button 
            onClick={handleUploadClick} 
            className="action-button">
            업로드
          </button>
        </div>
        <div className="action-group">
          <p className="action-text">친환경 퀴즈 풀기</p>
          <button 
            onClick={handleQuizClick} 
            className="action-button">
            풀기
          </button>
        </div>
      </main> */}
      </div>
    </div>
  );
};

export default Main;
