import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Checklist = ({ username }) => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState([]);  // 초기값을 빈 배열로 설정
  const [newTask, setNewTask] = useState("");

  useEffect(() => {
    if (!username) {
      navigate();  // username이 없으면 로그인 페이지로 리디렉트
      return;
    }

    const fetchTasks = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/checklist/user/${username}`, {
          headers: { 'Content-Type': 'application/json' }
        });

        // 응답이 배열인지 확인하고 배열이 아닌 경우 빈 배열로 처리
        const taskData = Array.isArray(response.data) ? response.data : [];
        setTasks(taskData);

      } catch (error) {
        console.error('Error fetching tasks:', error);
        setTasks([]);  // 에러 발생 시에도 빈 배열로 설정
      }
    };

    fetchTasks();
  }, [username, navigate]);

  // 새로운 작업 추가
  const addTask = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/checklist/add', 
        { username, text: newTask }, 
        { headers: { 'Content-Type': 'application/json' } }
      );
      setTasks([...tasks, response.data]);
      setNewTask("");
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  // 작업 삭제
  const deleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/checklist/delete/${id}`);
      setTasks(tasks.filter(task => task.id !== id));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h2>{`${username}'s Checklist`}</h2>
      <ul>
        {tasks.length > 0 ? (
          tasks.map(task => (
            <li key={task.id}>
              {task.text}
              <button onClick={() => deleteTask(task.id)}>Delete</button>
            </li>
          ))
        ) : (
          <li>No tasks available</li>
        )}
      </ul>
      <input 
        type="text" 
        value={newTask} 
        onChange={e => setNewTask(e.target.value)} 
        placeholder="New Task" 
      />
      <button onClick={addTask}>Add Task</button>
    </div>
  );
};

export default Checklist;


