import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, Navigate } from 'react-router-dom';
import './App.css';
import AuthPage from './components/auth/AuthPage';
import Main from './components/Main';
import ImageUploadCard from './components/card/ImageUploadCard';
import Quiz from './components/quiz/Quiz';
import Profile from './components/pages/Profile';
import DailyQuest from './components/pages/DailyQuest';
import BottomNavBar from './components/mobile/BottomNavBar';

const App = () => {
  const [userList, setUserList] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false); // 인증 상태 추가

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsAuthenticated(!!token);  // token이 있으면 true, 없으면 false
  }, []);

  const location = useLocation();

  return (
    <div className="App">
      <Routes>
        <Route path="/" element={<AuthPage setIsAuthenticated={setIsAuthenticated} />} />
        <ProtectedRoute path="/main" element={<Main setIsAuthenticated={setIsAuthenticated} />} isAuthenticated={isAuthenticated} />
        <ProtectedRoute path="/profile" element={<Profile />} isAuthenticated={isAuthenticated} />
        <ProtectedRoute path="/quest" element={<DailyQuest />} isAuthenticated={isAuthenticated} />
        <ProtectedRoute path="/imageupload" element={<ImageUploadCard />} isAuthenticated={isAuthenticated} />
        <ProtectedRoute path="/quiz" element={<Quiz />} isAuthenticated={isAuthenticated} />
        <Route path="/signup" element={<AuthPage userList={userList} setUserList={setUserList} />} />
        <Route path="/*" element={<Main setIsAuthenticated={setIsAuthenticated} />} />
      </Routes>
      {location.pathname !== '/' && <BottomNavBar />}
    </div>
  );
}

const RootApp = () => (
  <Router>
    <App />
  </Router>
);

export default RootApp;


