import React, { useState } from 'react';
import Header from '../../components/Header';
import '../styles/Settings.css';

function Settings() {
  const [notifications, setNotifications] = useState(true);
  const [language, setLanguage] = useState('ko');
  const [theme, setTheme] = useState('light');
  const [message, setMessage] = useState('');

  // 폼 제출 시 호출
  const handleSubmit = (e) => {
    e.preventDefault();
    // 백엔드 저장 로직
    console.log("설정 저장:", { notifications, language, theme });
    setMessage('설정이 성공적으로 저장되었습니다!');
  };

  // 테마가 변경되면 body 배경 색상 변경
  const handleThemeChange = (newTheme) => {
    setTheme(newTheme);
    document.body.style.backgroundColor = newTheme === 'light' ? '#ffffff' : '#333333';
    document.body.style.color = newTheme === 'light' ? '#000000' : '#ffffff';
  };

  return (
    <div className="settings-container">
      <Header />
      <h2>환경설정</h2>
      <form onSubmit={handleSubmit} className="settings-form">
        {/* 알림 설정 */}
        <div className="form-group">
          <label>
            <input 
              type="checkbox" 
              checked={notifications} 
              onChange={() => setNotifications(!notifications)} 
            />
            알림 받기
          </label>
        </div>

        {/* 언어 설정 */}
        <div className="form-group">
          <label>언어 설정:</label>
          <select 
            value={language} 
            onChange={(e) => setLanguage(e.target.value)}
          >
            <option value="ko">한국어</option>
            <option value="en">English</option>
            <option value="jp">日本語</option>
          </select>
        </div>

        {/* 테마 설정 */}
        <div className="form-group">
          <label>테마 설정:</label>
          <div className="radio-group">
            <label>
              <input 
                type="radio" 
                value="light" 
                checked={theme === 'light'} 
                onChange={() => handleThemeChange('light')} 
              />
              라이트 모드
            </label>
            <label>
              <input 
                type="radio" 
                value="dark" 
                checked={theme === 'dark'} 
                onChange={() => handleThemeChange('dark')} 
              />
              다크 모드
            </label>
          </div>
        </div>

        <button type="submit" className={`submit-btn ${theme}`}>저장</button>
      </form>

      {/* 성공 메시지 */}
      {message && <p className="success-message">{message}</p>}
    </div>
  );
}

export default Settings;
