import React from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../../components/Header'; // Header 컴포넌트 가져오기
import ProgressBar from '../components/main/ProgressBar'; // 경로 수정 필요
import '../styles/DailyQuest.css';


const DailyQuest = () => {
  const navigate = useNavigate();

  const handleTabClick = (page) => {
    navigate(page); // 페이지 이동
  };

  const quests = [
    { task: "대중 교통 후불 카드 인증", reward: 10 },
    { task: "친환경 퀴즈 풀기", reward: 10 },
    { task: "가까운 거리 자전거/도보 이용 인증", reward: 10 },
    { task: "에어컨 온도 인증", reward: 10 },
  ];

  return (
    <div>
      <Header /> {/* Header를 별도의 상위 요소로 배치 */}
      <div className="main-content"> {/* 상단 여백 추가 */}
        <div className="daily-quest"> {/* max-width를 가진 컨테이너 */}
        <h1>진행 상태</h1>
        {/* 진행 상태를 표시하는 ProgressBar 컴포넌트 */}
        <ProgressBar progress={progress} />

          {/* 일일 퀘스트 목록 */}
          <div className="quest-list">
            <h2>돌보미의 오늘 할 일</h2>
            <p>매일 자정에 초기화됩니다</p>
            {quests.map((quest, index) => (
              <div key={index} className="quest-item">
                <span className="task">{quest.task}</span>
                <span className="reward">{quest.reward} 포인트</span>
              </div>
            ))}
          </div>
      </div>
      </div>
    </div>
  );
};

export default DailyQuest;
