import React from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Header.css'; // 기본 스타일 (PC 전용)
import './mobile/Header.css'; // 모바일 전용 스타일
import Sidebar from './main/Sidebar';

function Header() {
  const navigate = useNavigate();

  // 사용자 아이콘 클릭 시 profile 페이지로 이동하는 함수
  const handleUserIconClick = () => {
    navigate('/profile');
  };

  return (
    <div>
      {/* PC에서만 보이는 사이드바 */}
      <div className="d-none d-md-block"> {/* d-none은 기본적으로 숨기고, d-md-block은 중간 크기 이상에서 보이도록 설정 */}
        <Sidebar />
      </div>

      {/* 헤더 */}
      <header className="app-header">
        <div className="header-content">
          <div className="header-icons">
            <i className="fas fa-bell"></i> {/* 알림 아이콘 */}
            <i
              className="fas fa-user"
              onClick={handleUserIconClick}
              style={{ cursor: 'pointer' }}
            ></i>
          </div>
        </div>
      </header>
    </div>
  );
}

export default Header;


