import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // useNavigate 임포트
import '../components/styles/AuthPage';  

function AuthPage() {
  const [isLogin, setIsLogin] = useState(true); // 로그인/회원가입 상태 관리
  const [username, setUsername] = useState(''); // 사용자 이름 상태
  const [password, setPassword] = useState(''); // 비밀번호 상태
  const [email, setEmail] = useState(''); // 이메일 상태

  const navigate = useNavigate(); // useNavigate 훅 사용

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/login', {
        username,
        password,
      });
      // 로그인 성공 시 MainPage로 이동
      console.log('Login successful:', response.data);
      navigate('/main'); // MainPage로 이동
    } catch (error) {
      console.error('Login failed:', error.response?.data || error.message);
      alert('Invalid username or password');
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/signup', {
        username,
        password,
        email,
      });
      console.log('Signup successful:', response.data);
      setIsLogin(true); // 회원가입 후 로그인 페이지로 이동
    } catch (error) {
      console.error('Signup failed:', error.response?.data || error.message);
      alert('Signup failed. Please try again.');
    }
  };

  return (
    <div className="login-wrap">
      <div className="login-html">

        {/* 로고가 들어갈 부분 */}
        <div className="logo-container">
          <img src="../assets/image/Logo.jpg" alt="Logo" className="logo" />
        </div>

        <input
          id="tab-1"
          type="radio"
          name="tab"
          className="sign-in"
          checked={isLogin}
          onChange={() => setIsLogin(true)}
        />
        <label htmlFor="tab-1" className="tab">로그인</label>
        <input
          id="tab-2"
          type="radio"
          name="tab"
          className="sign-up"
          checked={!isLogin}
          onChange={() => setIsLogin(false)}
        />
        <label htmlFor="tab-2" className="tab">회원가입</label>

        <div className="login-form">
          <div className="sign-in-htm" style={{ display: isLogin ? 'block' : 'none' }}>
            <form onSubmit={handleLogin}>
              <div className="group">
                <label htmlFor="user" className="label">Username</label>
                <input
                  id="user"
                  type="text"
                  className="input"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                />
              </div>
              <div className="group">
                <label htmlFor="pass" className="label">Password</label>
                <input
                  id="pass"
                  type="password"
                  className="input"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>
              <div className="group">
                <input id="check" type="checkbox" className="check" defaultChecked />
                <label htmlFor="check"><span className="icon"></span> 암호기억하기</label>
              </div>
              <div className="group">
                <input type="submit" className="button" value="Sign In" />
              </div>
            </form>
            <div className="hr"></div>
            <div className="social-login">
              <button className="social-button instagram-button">
                <i className="fab fa-instagram"></i> Sign in with Instagram
              </button>
              <button className="social-button google-button">
                <i className="fab fa-google"></i> Sign in with Google
              </button>
            </div>
          </div>
          <div className="sign-up-htm" style={{ display: !isLogin ? 'block' : 'none' }}>
            <form onSubmit={handleSignup}>
              <div className="group">
                <label htmlFor="user" className="label">Username</label>
                <input
                  id="user"
                  type="text"
                  className="input"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                />
              </div>
              <div className="group">
                <label htmlFor="pass" className="label">Password</label>
                <input
                  id="pass"
                  type="password"
                  className="input"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>
              <div className="group">
                <label htmlFor="pass-repeat" className="label">Repeat Password</label>
                <input
                  id="pass-repeat"
                  type="password"
                  className="input"
                />
              </div>
              <div className="group">
                <label htmlFor="email" className="label">Email Address</label>
                <input
                  id="email"
                  type="text"
                  className="input"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>
              <div className="group">
                <input type="submit" className="button" value="Sign Up" />
              </div>
            </form>
            <div className="hr"></div>
            <div className="foot-lnk">
              <label htmlFor="tab-1" onClick={() => setIsLogin(true)}>Already Member?</label>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AuthPage;


