import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../../components/Header';
import ProgressBar from '../../components/main/ProgressBar'; // ProgressBar Í∞ÄÏ†∏Ïò§Í∏∞
import '../mobile/DailyQuest.css';
import '../styles/DailyQuest.css';


const DailyQuest = () => {
  const navigate = useNavigate();
  // ÏßÑÌñâ ÏÉÅÌÉúÎ•º Í¥ÄÎ¶¨ÌïòÍ∏∞ ÏúÑÌïú ÏÉÅÌÉú Î≥ÄÏàò Ï∂îÍ∞Ä
  const [progress, setProgress] = useState(0); // ÏßÑÌñâÎ•† ÏÉÅÌÉú

  const handleTabClick = (page) => {
    navigate(page); // ÌéòÏù¥ÏßÄ Ïù¥Îèô
  };

  const quests = [
    { task: "ÎåÄÏ§ë ÍµêÌÜµ ÌõÑÎ∂à Ïπ¥Îìú Ïù∏Ï¶ù", reward: 10, link: "/imageupload" },
    { task: "ÏπúÌôòÍ≤Ω ÌÄ¥Ï¶à ÌíÄÍ∏∞", reward: 10, link: "/quiz"  },
    { task: "Í∞ÄÍπåÏö¥ Í±∞Î¶¨ ÏûêÏ†ÑÍ±∞/ÎèÑÎ≥¥ Ïù¥Ïö© Ïù∏Ï¶ù", reward: 10 },
    { task: "ÏóêÏñ¥Ïª® Ïò®ÎèÑ Ïù∏Ï¶ù", reward: 10 },
  ];

  // ÏòàÏãúÎ°ú ÏßÑÌñâÎ•†ÏùÑ ÏóÖÎç∞Ïù¥Ìä∏ÌïòÎäî Î°úÏßÅ
  useEffect(() => {
    const completedQuests = 2; // ÏôÑÎ£åÎêú ÌÄòÏä§Ìä∏ Ïàò
    const totalQuests = quests.length;
    const progressValue = (completedQuests / totalQuests) * 100;
    setProgress(progressValue);
  }, [quests]);

  return (
    <div>
      <Header />
      <div className="main-content">
        <div className="daily-quest">
          <div className="quest-list">
            <h3>üå± ÎèåÎ≥¥ÎØ∏Ïùò Ïò§Îäò Ìï† Ïùº üå±</h3>
            <p>Ï£ºÍ∞Ñ ÏûêÏ†ïÏóê Ï¥àÍ∏∞ÌôîÎê©ÎãàÎã§</p>
            {quests.map((quest, index) => (
              <div
                key={index}
                className="quest-item"
                onClick={() => {
                  if (quest.link) {
                    navigate(quest.link); // ÎßÅÌÅ¨Í∞Ä ÏûàÎäî ÌÄòÏä§Ìä∏Î•º ÌÅ¥Î¶≠ÌïòÎ©¥ Ìï¥Îãπ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
                  }
                }}
                style={{ cursor: quest.link ? 'pointer' : 'default' }} // ÎßÅÌÅ¨Í∞Ä ÏûàÎäî Ìï≠Î™©ÏóêÎßå Ïª§ÏÑú Ïä§ÌÉÄÏùºÏùÑ Î≥ÄÍ≤Ω
              >
                <span className="task">{quest.task}</span>
                <span className="reward">{quest.reward} Ìè¨Ïù∏Ìä∏</span>
              </div>
            ))}
            <h2>ÏßÑÌñâ ÏÉÅÌÉú</h2>
            {/* ÏßÑÌñâ ÏÉÅÌÉúÎ•º ÌëúÏãúÌïòÎäî ProgressBar Ïª¥Ìè¨ÎÑåÌä∏ */}
            <ProgressBar progress={progress} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default DailyQuest;
