import React, { useState, useEffect } from 'react';
import '../styles/Quiz.css'; // 스타일을 위한 별도의 CSS 파일

function Quiz() {
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(true); // 로딩 상태 추가
    const [error, setError] = useState(null); // 에러 상태 추가

    useEffect(() => {
        // API 호출로 퀴즈 질문 가져오기
        fetch('/api/quiz')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                setQuestions(data);
                setLoading(false); // 데이터 로드 완료
            })
            .catch(error => {
                setError(error);
                setLoading(false); // 데이터 로드 실패
            });
    }, []);

    const handleAnswer = (userAnswer) => {
        const correct = questions[currentQuestionIndex].answer === userAnswer;
        setResult(correct ? 'Correct!' : 'Incorrect!');
        
        // 다음 질문으로 이동
        setTimeout(() => {
            setResult(null);
            if (currentQuestionIndex + 1 < questions.length) {
                setCurrentQuestionIndex(currentQuestionIndex + 1);
            } else {
                alert('Quiz Completed!');
            }
        }, 1000);
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error.message}</div>; // 에러 메시지 표시

    return (
        <div className="quiz-container">
            <h2 className="question">{questions[currentQuestionIndex].qText}</h2>
            <div className="answer-buttons">
                <div className="answer-box" onClick={() => handleAnswer(true)}>O</div>
                <div className="answer-box" onClick={() => handleAnswer(false)}>X</div>
            </div>
            {result && <p className="result">{result}</p>}
        </div>
    );
}

export default Quiz;





