import React, { useState, useEffect } from 'react';

function Quiz() {
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [answers, setAnswers] = useState([]);
    const [score, setScore] = useState(null);

    useEffect(() => {
        fetch('/api/quizzes')
            .then(response => response.json())
            .then(data => setQuestions(data));
    }, []);

    const handleAnswer = (userAnswer) => {
        const currentQuestion = questions[currentQuestionIndex];
        setAnswers([...answers, { questionId: currentQuestion.id, userAnswer }]);
        
        if (currentQuestionIndex + 1 < questions.length) {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
        } else {
            submitAnswers();
        }
    };

    const submitAnswers = () => {
        fetch('/api/quizzes/submit', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(answers),
        })
        .then(response => response.json())
        .then(score => setScore(score));
    };

    if (questions.length === 0) return <div>Loading...</div>;
    if (score !== null) return <div>Your score is: {score}/{questions.length}</div>;

    return (
        <div>
            <h2>{questions[currentQuestionIndex].question}</h2>
            <button onClick={() => handleAnswer(true)}>O</button>
            <button onClick={() => handleAnswer(false)}>X</button>
        </div>
    );
}

export default Quiz;
