import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, Navigate } from 'react-router-dom';
import './App.css';
import AuthPage from './components/auth/AuthPage';
import Main from './components/Main';
import ImageUploadCard from './components/card/ImageUploadCard';
import Quiz from './components/quiz/Quiz';
import Profile from './components/pages/Profile';
import DailyQuest from './components/pages/DailyQuest';
import BottomNavBar from './components/mobile/BottomNavBar';
import { jwtDecode } from 'jwt-decode'; // JWT 디코딩 라이브러리 추가

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // 초기값 false

  // 컴포넌트가 마운트될 때 로컬 스토리지에서 토큰 확인
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decodedToken = jwtDecode(token);
      // 여기서 토큰의 유효성을 확인하거나 필요한 정보를 확인할 수 있습니다.
      setIsAuthenticated(true);
    }
  }, []);

  return (
    <div className="App">
      <Routes>
        <Route path="/" element={<AuthPage setIsAuthenticated={setIsAuthenticated} />} />
        <Route path="/main" element={isAuthenticated ? <Main setIsAuthenticated={setIsAuthenticated} /> : <Navigate to="/" />} />
        <Route path="/signup" element={<AuthPage setIsAuthenticated={setIsAuthenticated} />} />
        <Route path="/profile" element={<Profile />} />
        <Route path="/quest" element={<DailyQuest />} />
        <Route path="/imageupload" element={<ImageUploadCard />} />
        <Route path="/quiz" element={<Quiz />} />
      </Routes>
      {isAuthenticated && <BottomNavBar />}
    </div>
  );
}

const RootApp = () => (
  <Router>
    <App />
  </Router>
);

export default RootApp;

