import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Axios를 사용하여 HTTP 요청을 보냄
import '../components/styles/AuthPage.css'; // 스타일을 유지

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true); // 로그인/회원가입 상태 관리
  const [email, setEmail] = useState(''); // 이메일 상태
  const [password, setPassword] = useState(''); // 비밀번호 상태
  const [username, setUsername] = useState(''); // 회원가입을 위한 사용자 이름 상태

  const navigate = useNavigate(); // 페이지 이동을 위한 네비게이션

  // 로그인 처리 함수
  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // 스프링 백엔드의 로그인 API로 POST 요청
      const response = await axios.post('/api/auth/login', {
        emailId: email,
        password: password,
      });

      // 로그인 성공 시 처리 (예: 토큰 저장, 페이지 이동 등)
      console.log('로그인 성공:', response.data);
      navigate('/main'); // 로그인 성공 시 메인 페이지로 이동
    } catch (error) {
      // 로그인 실패 시 처리
      console.error('로그인 실패:', error.response ? error.response.data : error.message);
      alert('로그인에 실패했습니다. 다시 시도해주세요.');
    }
  };

  // 회원가입 처리 함수
  const handleSignup = async (e) => {
    e.preventDefault();

    try {
      // 스프링 백엔드의 회원가입 API로 POST 요청
      const response = await axios.post('/api/auth/signup', {
        username: username,
        email: email,
        password: password,
      });

      // 회원가입 성공 시 처리
      console.log('회원가입 성공:', response.data);
      setIsLogin(true); // 회원가입 성공 시 로그인 폼으로 전환
    } catch (error) {
      // 회원가입 실패 시 처리
      console.error('회원가입 실패:', error.response ? error.response.data : error.message);
      alert('회원가입에 실패했습니다. 다시 시도해주세요.');
    }
  };

  return (
    <div className="login-wrap">
      <div className="login-html">
        <div className="social-login">
          <button className="social-button instagram-button">
            <i className="fab fa-instagram"></i>
          </button>
          <button className="social-button google-button">
            <i className="fab fa-google"></i>
          </button>
          <button className="social-button facebook-button">
            <i className="fab fa-facebook-f"></i>
          </button>
        </div>

        <div className="or-separator">
          <span>or</span>
        </div>

        {/* 로그인 폼 */}
        <div className="login-form">
          <div className="sign-in-htm" style={{ display: isLogin ? 'block' : 'none' }}>
            <form onSubmit={handleLogin}>
              <div className="group">
                <label htmlFor="login-email" className="label">Email</label>
                <input
                  id="login-email"
                  type="email"
                  className="input"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <div className="group">
                <label htmlFor="login-password" className="label">Password</label>
                <input
                  id="login-password"
                  type="password"
                  className="input"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <div className="group">
                <input id="check" type="checkbox" className="check" defaultChecked />
                <label htmlFor="check"><span className="icon"></span> 암호 기억하기</label>
              </div>
              <div className="group">
                <input type="submit" className="button" value="Sign In" />
              </div>
            </form>

            {/* '계정이 없나요?' 문구 추가 */}
            <div className="foot-lnk">
              <p>계정이 없나요? <span onClick={() => setIsLogin(false)} style={{ color: 'orange', cursor: 'pointer' }}>Create Account</span></p>
            </div>
          </div>

          {/* 회원가입 폼 */}
          <div className="sign-up-htm" style={{ display: !isLogin ? 'block' : 'none' }}>
            <form onSubmit={handleSignup}>
              <div className="group">
                <label htmlFor="signup-username" className="label">Username</label>
                <input
                  id="signup-username"
                  type="text"
                  className="input"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="group">
                <label htmlFor="signup-email" className="label">Email</label>
                <input
                  id="signup-email"
                  type="email"
                  className="input"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <div className="group">
                <label htmlFor="signup-password" className="label">Password</label>
                <input
                  id="signup-password"
                  type="password"
                  className="input"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <div className="group">
                <input type="submit" className="button" value="Sign Up" />
              </div>
            </form>

            {/* '이미 계정이 있나요?' 문구 추가 */}
            <div className="foot-lnk">
              <p>이미 계정이 있나요? <span onClick={() => setIsLogin(true)} style={{ color: 'orange', cursor: 'pointer' }}>Sign In</span></p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
