import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Sidebar.css';

function Sidebar() {
  const navigate = useNavigate();
  const location = useLocation();
  const [active, setActive] = useState(location.pathname);

  useEffect(() => {
    // 페이지가 변경될 때 active 상태를 업데이트
    setActive(location.pathname);
  }, [location]);

  const handleNavigation = (path) => {
    navigate(path);
  };

  const handleLogout = () => {
    // 로그아웃 처리 (예: 로컬 스토리지에서 토큰 제거)
    localStorage.removeItem('authToken'); // 인증 토큰 삭제
    navigate('/'); // 로그인 페이지로 이동
  };

  return (
    <div className="sidebar">
      <h1 className="logo-title">
        <span>Energy</span>
        <i className="fas fa-bolt logo-icon"></i> {/* 번개 아이콘 */}
        <span>Save</span>
      </h1>
      <ul className="sidebar-menu">
        <li
          className={active === '/home' ? 'active' : ''}
          onClick={() => handleNavigation('/home')}>
          <i className="fas fa-home"></i> Home
        </li>
        <li
          className={active === '/calendar' ? 'active' : ''}
          onClick={() => handleNavigation('/calendar')}>
          <i className="fas fa-calendar-alt"></i> Calendar
        </li>
        <li
          className={active === '/dailyquest' ? 'active' : ''}
          onClick={() => handleNavigation('/dailyquest')}>
          <i className="fas fa-list-ul"></i> DailyQuest
        </li>
        <li
          className={active === '/settings' ? 'active' : ''}
          onClick={() => handleNavigation('/settings')}>
          <i className="fas fa-cogs"></i> Settings
        </li>
        <button className="logout-button" onClick={handleLogout}>
        <i className="fas fa-sign-out-alt"></i> Logout
        </button>
      </ul>
    </div>
  );
}

export default Sidebar;
