import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Sidebar.css';

function Sidebar() {
  const navigate = useNavigate();
  const location = useLocation();
  const [active, setActive] = useState(location.pathname); // 현재 경로로 초기화

  const handleNavigation = (path) => {
    navigate(path);
    setActive(path); // 클릭된 항목을 활성화
  };

  return (
    <div className="sidebar">
      <h1 className="logo-title">
        <span>Energy</span>
        <i className="fas fa-bolt logo-icon"></i> {/* 번개 아이콘 */}
        <span>Save</span>
      </h1>
      <ul className="sidebar-menu">
        <li
          className={active === '/main/home' ? 'active' : ''}
          onClick={() => handleNavigation('/home')}
        >
          <i className="fas fa-home"></i> Home
        </li>
        <li
          className={active === '/main/calendar' ? 'active' : ''}
          onClick={() => handleNavigation('/main/calendar')}
        >
          <i className="fas fa-calendar-alt"></i> Calendar
        </li>
        <li
          className={active === '/main/dailyquest' ? 'active' : ''}
          onClick={() => handleNavigation('/main/dailyquest')}
        >
          <i className="fas fa-calendar-alt"></i> DailyQuest
        </li>
        <li
          className={active === '/main/settings' ? 'active' : ''}
          onClick={() => handleNavigation('/main/settings')}
        >
          <i className="fas fa-cogs"></i> Settings
        </li>
      </ul>
    </div>
  );
}

export default Sidebar;
